import React, { Component } from 'react'

export class NewsItem extends Component {
    render() {
        let randomURLS = [
            'https://f8n-production.s3.amazonaws.com/creators/profile/j4bdzvouc-325235-jpeg-5d4c8s.jpeg', 
            'https://static.photocdn.pt/images/articles/2019/02/28/Best_Variable_ND_Filters_of_2019.jpg', 
            '3128749',
        ];  
        let {title, description, imageUrl, newsUrl} = this.props;
        return (
            <div className='my-3'>
                <div className="card mx-auto">
                    <a href={newsUrl} target="_blank" rel='noreferrer' className='newslinks'>
                        <img src={!imageUrl?"":imageUrl} className="card-img-top" alt="..." />
                        <div className="card-body">
                            <h5 className="card-title">{title}</h5>
                            <div id='description'>
                                <p className="card-text">{description}</p>
                            </div>
                        </div>
                    </a>
                </div>
            </div>
        )
    }
}

export default NewsItem
